-- 데이터 확인용 쿼리
SELECT
    a.uuid as accident_id,
    a.type as accident_type,
    a.created_at as timestamp,
    ar.uuid as area_uuid,
    ar.area_name,
    u.user_id,
    u.user_name,
    c.company_name
FROM accident a
    INNER JOIN area ar ON a.area_uuid = ar.uuid
    INNER JOIN "users" u ON a.user_uuid = u.uuid
    INNER JOIN company c ON u.company_uuid = c.uuid
ORDER BY a.created_at DESC;
